const items = $input.all();

return items.map(item => {
  const bin = item.binary?.data || {};

  const directory = bin.directory || "";
  const fileName  = bin.fileName  || "";

  const path = directory
    ? `${directory}/${fileName}`
    : fileName;

  const safeName = path
    .replace(/^\.+/, '')
    .replace(/[\\/]+/g, '_')
    || fileName;

  return {
    json: {
      fileName: safeName,
      directory,
      path,
    },
    binary: {
      ...item.binary,
      data: {
        ...bin,
        fileName: safeName,
      }
    }
  };
});

function thaiToLatin(input) {
  if (!input) return '';

  const THAI_DIGITS = '๐๑๒๓๔๕๖๗๘๙';

  const charMap = {
    'ก': 'k','ข': 'kh','ฃ': 'kh','ค': 'kh','ฅ': 'kh','ฆ': 'kh',
    'ง': 'ng',
    'จ': 'ch','ฉ': 'ch','ช': 'ch','ซ': 's','ฌ': 'ch',
    'ญ': 'y',
    'ฎ': 'd','ฏ': 't',
    'ฐ': 'th','ฑ': 'th','ฒ': 'th','ถ': 'th','ท': 'th','ธ': 'th',
    'ณ': 'n','ด': 'd','ต': 't','น': 'n',
    'บ': 'b','ป': 'p','ผ': 'ph','ฝ': 'f','พ': 'ph','ฟ': 'f','ภ': 'ph',
    'ม': 'm','ย': 'y','ร': 'r','ล': 'l','ว': 'w',
    'ศ': 's','ษ': 's','ส': 's','ห': 'h','ฮ': 'h',
    'อ': 'o',
    'ะ': 'a','ั': 'a','า': 'a','ำ': 'am',
    'ิ': 'i','ี': 'i',
    'ุ': 'u','ู': 'u',
    'ึ': 'ue','ื': 'ue',
    'เ': 'e','แ': 'ae','โ': 'o','ใ': 'ai','ไ': 'ai',
    '่': '', '้': '', '๊': '', '๋': '',
    '์': '', 'ํ': '', 'ฯ': '', 'ๆ': '',
    'ฺ': '', '฿': '',
  };

  let out = '';

  for (const ch of input.normalize('NFC')) {

    // English / number
    if (/^[a-zA-Z0-9]$/.test(ch)) {
      out += ch;
      continue;
    }

    // Thai digit → western digit
    const idx = THAI_DIGITS.indexOf(ch);
    if (idx !== -1) {
      out += idx;
      continue;
    }

    // Thai character mapping
    if (ch in charMap) {
      out += charMap[ch];
      continue;
    }

    // Whitespace
    if (/\s/.test(ch)) {
      out += ' ';
      continue;
    }

    // others → space
    out += ' ';
  }

  return out.replace(/\s+/g, ' ').trim();
}

// -------------------------------------
// slugPart
// -------------------------------------
function slugPart(part) {
  const latin = thaiToLatin(part || '');
  return latin
    .toLowerCase()
    .replace(/[^a-z0-9]+/g, '_')
    .replace(/^_+|_+$/g, '')
    .slice(0, 8) || 'p';
}

// -------------------------------------
// FNV-1a 32-bit hash (no crypto needed)
// -------------------------------------
function fnv1a(str) {
  let hash = 0x811c9dc5;
  for (let i = 0; i < str.length; i++) {
    hash ^= str.charCodeAt(i);
    hash = (hash >>> 0) * 0x01000193; // 16777619
  }
  return (hash >>> 0).toString(16).padStart(8, '0');
}

// -------------------------------------
// MAIN
// -------------------------------------
const items = $input.all();
if (!items.length) return [];

const dirRaw = String(items[0].json.directory || '');

// split by / or \
const parts = dirRaw.split(/[\\/]/).filter(Boolean);

// last 2 folders → slug
const slug = parts.slice(-2).map(slugPart).join('_') || 'schema';

// hash from full normalized path
const normalizedPath = parts.map(p => p.normalize('NFC')).join('/');
const hash8 = fnv1a(normalizedPath).slice(0, 8);

// final schema (safe for Postgres)
let schema = `s_${slug}_${hash8}`
  .toLowerCase()
  .replace(/[^a-z0-9_]/g, '_')
  .slice(0, 63);

// output
return items.map(item => ({
  json: {
    ...item.json,
    schema,
  },
  binary: item.binary,
}));
