// ===== Helpers =====

//   
const THAI_DIGITS = "";
function toWesternDigits(s) {
  return String(s ?? "").replace(/[-]/g, d => String(THAI_DIGITS.indexOf(d)));
}

//    space 
function cleanSpaces(s) {
  return String(s ?? "")
    .replace(/[\u00A0\u1680\u2000-\u200D\u202F\u205F\u3000\uFEFF]/g, " ")
    .replace(/[\t\r\n]+/g, " ")
    .replace(/\s+/g, " ")
    .trim();
}

//  ""  sourceString  targetString 
function isSubset(sourceString, targetString) {
  if (!sourceString || !targetString) return false;

  //  Source
  const sourceWords = sourceString.split(" ").filter(w => w.trim().length > 0);

  //  Target  ( Target )
  const targetCombined = targetString.replace(/\s+/g, "");

  // 
  return sourceWords.every(word => targetCombined.includes(word));
}

// -----------------------------------------------------------

// Normalize : /  ""
function normalizeCompanyName(name) {
  if (!name) return null;
  let s = toWesternDigits(String(name));
  s = cleanSpaces(s);

  //  ()
  //  "", "", "" 
  s = s.replace(/(|||\.|||\.||\.|\.|)/g, "");

  //  (.)  
  s = s.replace(/[.,\-]/g, " ");

  return cleanSpaces(s);
}

// Normalize :   ""
function normalizeThaiAddress(raw) {
  if (!raw) return null;
  let s = toWesternDigits(String(raw));
  s = cleanSpaces(s);

  // 
  s = s.replace(/^(|)\s*\.?\s*/g, "");

  //   
  s = s.replace(/\.\s*(\d+)/g, " $1");
  s = s.replace(/\s*(\d+)/g, " $1");
  s = s.replace(/\.\s*/g, " ");
  s = s.replace(/\.\s*/g, " ");
  s = s.replace(/\.\s*/g, " ");
  s = s.replace(/\.\s*/g, " ");

  //  ., -
  s = s.replace(/[.,\-]/g, " ");

  // *** :  .replace(/\s+/g, "")  ***
  return cleanSpaces(s);
}

// ===== Main =====

//  input
if (items.length < 2) {
    return [{ json: { error: "Expecting at least 2 items (PDF and DB)" } }];
}

const pdf = items[0].json; //  PDF
const db  = items[1].json; //  DB/API

// 1) Normalize Name
const name_pdf = normalizeCompanyName(pdf.buyer_name);
const name_db  = normalizeCompanyName(db.company_name);

// Logic :  2  (PDF  DB  DB  PDF)
let company_name_status = false;
if (name_pdf && name_db) {
    company_name_status = isSubset(name_pdf, name_db) || isSubset(name_db, name_pdf);
}

// 2) Normalize Address
const addr_pdf = normalizeThaiAddress(pdf.buyer_address_raw);
const addr_db  = normalizeThaiAddress(db.address);

// Logic :  2 
let address_status = false;
if (addr_pdf && addr_db) {
    address_status = isSubset(addr_pdf, addr_db) || isSubset(addr_db, addr_pdf);
}

// ---- Output ----

return [
  {
    json: {
      ...pdf,
      //  DB 
      db_company_name: db.company_name,
      db_address: db.address,

      //  Normalize  ( Debug )
      norm_name_pdf: name_pdf,
      norm_name_db: name_db,
      norm_addr_pdf: addr_pdf,
      norm_addr_db: addr_db,

      //  True/False
      company_name_status,
      address_status
    }
  }
];
